import {getProductImagesById, getProductsSpecs} from "../api/productsData";

export const BuyCard = ({item}) => {

    const [count, setCount] = useState(0);
    const [images, setImages] = useState([]);
    const [specs, setSpecs] = useState([]);
    useEffect(() => {
        getProductImagesById(params.id).then(resI => {
                getProductsSpecs(params.id, res.data.data.result[0].category_id).then(resS => {
                    setImages(resI.data.data.result)
                    setSpecs(resS.data.data.result[0])
                })
            }
        )
    }, [])

    console.log(images, specs)



    return (
        <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src="holder.js/100px180?text=Image cap" />
            <Card.Body>
                <Card.Title>Card Title</Card.Title>
                <Card.Text>
                    Some quick example text to build on the card title and make up the
                    bulk of the card's content.
                </Card.Text>
            </Card.Body>
            <ListGroup className="list-group-flush">
                <ListGroup.Item>Cras justo odio</ListGroup.Item>
                <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
            </ListGroup>
            <Card.Body>
                <Card.Link href="#">Card Link</Card.Link>
                <Card.Link href="#">Another Link</Card.Link>
            </Card.Body>
        </Card>
    )
}
