import Container from "react-bootstrap/Container";
import {useContext, useEffect, useState} from "react";
import {DataContext} from "../../../context/context";
import {getProductById} from "../../../api/productsData";
import {BuyCard} from "../../../components/BuyCard";

export const Cart = () => {

    const context = useContext(DataContext)
    const [products, setProducts] = useState([])
    const productsIds = JSON.parse(localStorage.getItem(`${context.user.email}`));

    useEffect(() => {
        for (let i = 0; i < productsIds.length; i++) {
            getProductById(productsIds[i]).then(res => {
                setProducts([...products, ...res.data.data.result])
            })
        }

    }, [])

    console.log(products)
    return(
        <Container>
            {products.length &&
            products.map(item => <BuyCard item={item}/>)

            }
        </Container>
    )
}
