import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import {useEffect, useState} from "react";
import {getProductImagesById} from "../../dataHooks/useProductsData";

export const ShortCard = ({item}) => {

    const [images, setImages] = useState(['./images/gow.jpeg'])

    useEffect(() => {
        let data;
        getProductImagesById(item.id).then((res) => {
            if(res.data.data.result){
                setImages([...res.data.data.result.values])
            }
        });
    }, [])


    return (
        <Card style={{width: '18rem'}}>
            <Card.Img variant="top" src={images[0]}/>
            <Card.Body>
                <Card.Title>{item.title}</Card.Title>
                <Card.Text>
                    {item.short_description}
                </Card.Text>
                <Button variant="dark">Add to wishlist</Button>
                <Button variant="dark">Add to cart</Button>
            </Card.Body>
        </Card>
    );
}
