import Container from "react-bootstrap/Container";
import {AdvertSlider} from "../../../components/AdvertSlider/AdvertSlider";
import axios from "axios";
import {useEffect, useState} from "react";
import {getAllProducts, getProductImagesById} from "../../../dataHooks/useProductsData";
import {ShortCard} from "../../../components/ShortCard/ShortCard";

export const MainPage = () => {

    const [products, setProducts] = useState([]);
    const [images, setImages] = useState([]);

    useEffect(() => {
        getAllProducts().then((res, rej) => {
            setProducts([...res.data.data.result])
        })

    }, [])
    console.log(products)

    return(
        <Container>
            <AdvertSlider/>
            <Container>
                {products.length &&
                products.map(item => <ShortCard key={item.id} item={item}/>)
                }
            </Container>
        </Container>
    )
}
