import axios from "axios";
import {getProductById} from "./productsData";
// axios.defaults.withCredentials = true;

export const userLogin = async (email, password) => {
    const response = await axios.post('api/v1/users/login', {
        email,
        password
    })

    return response
};
export const userAddToWishList = async (user_id, product_id) => {
    const response = await axios.post('api/v1/users/wishlist', {
        user_id,
        product_id
    })

    return response
};
export const userGetWishList = async (user_id) => {
    // const data = [];
    const data = await Promise.all(() => {
        axios.get(`api/v1/users/wishlist/${user_id}`).then(res => {
            res.data.data.result.map(item => {
                getProductById(item.product_id).then(res => {
                    let item = res.data.data.result[0]
                    data.push(item)
                })
            })
        })
    })
    return data;
}
