import axios from "axios";
axios.defaults.withCredentials = true;


export const getAllProducts = async () => {
    const response = await axios.get('/api/v1/products')

    return response
}
export const getProductById = async (id) => {

    const response = await axios.get(`/api/v1/products/${id}`)

    return response
}
export const getProductImagesById = async (id) => {
    const response = await axios.get(`/api/v1/products?image=${id}`)

    return response
}
export const getProductsSpecs = async (id, category_id) => {
    const response = await axios.get(`/api/v1/products?category=${category_id}&id=${id}`)

    return response
}

export const addToCartLocally = (user_name, product_id) => {
    const storage = JSON.parse(localStorage.getItem(user_name));
    if(localStorage.getItem(user_name)){
        storage.unshift(product_id);
        localStorage.setItem(user_name, JSON.stringify(storage));
    }else{
        localStorage.setItem(user_name, JSON.stringify([product_id]))
    }


}
