import {useEffect, useState} from "react";
import {getProductById, getProductImagesById, getProductsSpecs} from "../api/productsData";
import {Card, ListGroup} from "react-bootstrap";

export const BuyCard = ({id}) => {

    const [item, setItem] = useState({});
    const [count, setCount] = useState(0);
    const [images, setImages] = useState([]);
    const [specs, setSpecs] = useState([]);
    useEffect(() => {
        getProductById(id).then(res => {
            getProductImagesById(id).then(resI => {
                    getProductsSpecs(id, res.data.data.result[0].category_id).then(resS => {
                        setImages(resI.data.data.result)
                        setItem(res.data.data.result[0])
                        setSpecs(resS.data.data.result[0])
                    })
                }
            )
        })
    }, [])

    return (
        <Card style={{ width: '25%' }}>
            <Card.Img variant="top" src="holder.js/100px180?text=Image cap" />
            <Card.Body>
                <Card.Title>{item.title}</Card.Title>
                <Card.Text>
                    Some quick example text to build on the card title and make up the
                    bulk of the card's content.
                </Card.Text>
            </Card.Body>
            <ListGroup className="list-group-flush">
                <ListGroup.Item>Cras justo odio</ListGroup.Item>
                <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
            </ListGroup>
            <Card.Body>
                <Card.Link href="#">Card Link</Card.Link>
                <Card.Link href="#">Another Link</Card.Link>
            </Card.Body>
        </Card>
    )
}
