import {useEffect, useState} from "react";
import {getProductById, getProductImagesById, getProductsSpecs} from "../api/productsData";
import {Card, ListGroup} from "react-bootstrap";

export const BuyCard = ({id}) => {

    const [item, setItem] = useState({});
    const [count, setCount] = useState(0);
    const [images, setImages] = useState([]);
    const [specs, setSpecs] = useState([]);
    useEffect(() => {
        getProductById(id).then(res => {
            getProductImagesById(id).then(resI => {
                    getProductsSpecs(id, res.data.data.result[0].category_id).then(resS => {
                        setImages(resI.data.data.result)
                        console.log(resI.data.data.result)
                        setItem(res.data.data.result[0])
                        setSpecs(resS.data.data.result[0])
                    })
                }
            )
        })
    }, [])

    return (
        <Card style={{ width: '45%' }}>
            <Card.Img variant="top" src={images[0].img} width={50}/>
            <Card.Body>
                <Card.Title>{item.title}</Card.Title>
                <Card.Text>
                    {item.short_description}
                </Card.Text>
            </Card.Body>
            <ListGroup className="list-group-flush">
                <ListGroup.Item>Price per unit: {item.price}$</ListGroup.Item>
                <ListGroup.Item>Total price {123}</ListGroup.Item>
            </ListGroup>
            <Card.Body>
                <Card.Link>Delete from cart</Card.Link>
            </Card.Body>
        </Card>
    )
}
